import { createContext, useState } from "react";
import { v4 as uuidv4 } from 'uuid';

export const EmployeeContext = createContext()

const EmployeeContextProvider = (props) => {
    const [employees, setEmployees] = useState([
        { id: uuidv4(), name: 'Ankita Saha', age: 26, address: 'Kolkata', gender: 'Female', about: 'developer' },
        { id: uuidv4(), name: 'Ananya Mondal', age: 27, address: 'Jodhpur', gender: 'Female', about: 'developer' },
        { id: uuidv4(), name: 'Chhaya Saha', age: 40, address: 'Kolkata', gender: 'Female', about: 'developer' },
        { id: uuidv4(), name: 'Samir Saha', age: 50, address: 'Chennai', gender: 'Male', about: 'developer' }
    ])

    const addEmployee = (name, age, address, gender, about) => {
        setEmployees([...employees, { id: uuidv4(), name, age, address, gender, about }])
    }
    const deleteEmployee = (id) => {
        setEmployees(employees.filter(employee => employee.id !== id))
    }

    const updateEmployee = (id, updateEmployee) => {
        setEmployees(employees.map((employee) => employee.id === id ? updatedEmployee : employee))
    }
    return (
        <EmployeeContext.Provider value={{ employees, addEmployee, deleteEmployee }}>
            {props.children}
        </EmployeeContext.Provider>
    )
}
export default EmployeeContextProvider;
