import { Button } from "react-bootstrap";
import { EmployeeContext } from '../contexts/EmployeeContext';
import { useContext, useState } from 'react';

const EditForm = ({ theEmployee }) => {

    const id = theEmployee.id;

    const [name, setName] = useState(theEmployee.name);
    const [age, setAge] = useState(theEmployee.age);
    const [address, setAddress] = useState(theEmployee.address);
    const [gender, setGender] = useState(theEmployee.gender);
    const [about, setAbout] = useState(theEmployee.about);
    const [image, setImage] = useState(theEmployee.image);

    const { updateEmployee } = useContext(EmployeeContext);
    const updatedEmployee = { id, name, age, address, gender, about }

    const onImageChange = async e => {
        setImage({ ...image, image: await convertBase64(e.target.files[0]) })
    }
    const convertBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file)
            fileReader.onload = () => {
                resolve(fileReader.result);
            }
            fileReader.onerror = (error) => {
                reject(error);
            }
        })
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        updateEmployee(id, updatedEmployee)
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Name *"
                    name="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Age *"
                    name="age"
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                    required
                />
                <select className="form-group" name="address" value={address} onChange={(e) => setAddress(e.target.value)}>
                    <option className="form-control">Select your location</option>
                    <option className="form-control">Chennai</option>
                    <option className="form-control">Kolkata</option>
                    <option className="form-control">Delhi</option>
                </select>
                <div className="form-group" name="gender" value={gender} onChange={(e) => setGender(e.target.value)}>
                    <input type="radio" id="male" name="gender" value="Male" />
                    <label htmlFor="Male">Male</label>
                    <input type="radio" id="female" name="gender" value="Female" />
                    <label htmlFor="Female">Female</label>
                </div>
                <input
                    type="text"
                    className="form-control"
                    placeholder="About *"
                    name="about"
                    value={about}
                    onChange={(e) => setAbout(e.target.value)}
                    required
                />
                <div className="form-group">
                    <input type="file" multiple className="form-control" name="image" id="file" onChange={e => onImageChange(e)} />
                </div>
            </div>
            <Button variant="success" type="submit">Edit Employee</Button>
        </form>
    )
}
export default EditForm;
