import { Modal, Button, Table } from 'react-bootstrap';
import { useContext, useState, useEffect } from "react";
import { EmployeeContext } from "../contexts/EmployeeContext";
// import Employee from "./Employee";
import AddForm from "./AddForm";
import { Link } from "react-router-dom";

const EmployeeList = () => {
    const { employees } = useContext(EmployeeContext);
    const { deleteEmployee } = useContext(EmployeeContext)

    const [show, setShow] = useState(false);

    // const handleShow = () => setShow(true);
    const handleClose = () => setShow(false);

    useEffect(() => {
        handleClose()
    }, [employees])

    return (
        <>
            <div className="table-title">
                <div className="row">
                    <div className="col-sm-6 col-12">
                        <h2>Manage Employees</h2>
                    </div>
                    <div className="col-sm-6 col-12">
                        <Link to="/">
                            <Button className="btn btn-success" ><i className="material-icons">&#xE147;</i> <span>Add New Employee</span></Button>
                        </Link>
                    </div>
                </div>
            </div>
            <div>
                <Table responsive="sm">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Address</th>
                            <th>Gender</th>
                            <th>About</th>
                            <th>Image</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>

                        {
                            employees.map((employee, index) => (
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>{employee.name}</td>
                                    <td>{employee.age}</td>
                                    <td>{employee.address}</td>
                                    <td>{employee.gender}</td>
                                    <td>{employee.about}</td>
                                    <td><img style={{ height: 100, width: 100 }} src={employee.image} alt="IMG" /></td>
                                    <td>
                                        <Button className="btn btn-success" data-toggle="modal"><span>Edit</span></Button>
                                        <Button onClick={() => deleteEmployee(employee.id)} className="btn btn-danger" data-toggle="modal"><span>Delete</span></Button>
                                    </td>
                                </tr>
                            ))
                        }
                    </tbody>
                </Table>
            </div>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add Employee</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <AddForm />
                </Modal.Body>

            </Modal>
        </>
    )
}

export default EmployeeList;
