import { Form, Button } from "react-bootstrap";
import { EmployeeContext } from '../contexts/EmployeeContext';
import { useContext, useState } from 'react';

const AddForm = () => {
    const { addEmployee } = useContext(EmployeeContext);

    const [newEmployee, setNewEmployee] = useState({
        name: "", age: "", address: "", gender: "", about: ""
    });

    const onInputChange = (e) => {
        setNewEmployee({ ...newEmployee, [e.target.name]: e.target.value })
    }

    const { name, age, address, gender, about } = newEmployee;

    const handleSubmit = (e) => {
        e.preventDefault();
        addEmployee(name, age, address, gender, about);
    }
    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group>
                <Form.Control
                    type="text"
                    placeholder="Name *"
                    name="name"
                    value={name}
                    onChange={(e) => onInputChange(e)}
                    required
                />
                <Form.Control
                    type="text"
                    placeholder="Age *"
                    name="age"
                    value={age}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <Form.Control
                    type="text"
                    placeholder="Address *"
                    name="address"
                    value={address}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <Form.Control
                    type="text"
                    placeholder="Gender *"
                    name="gender"
                    value={gender}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <Form.Control
                    type="text"
                    placeholder="About *"
                    name="about"
                    value={about}
                    required
                    onChange={(e) => onInputChange(e)}
                />
            </Form.Group>
            <Button variant="success" type="submit">Add New Employee</Button>
        </Form>
    )
}
export default AddForm;
