import { Button } from "react-bootstrap";
import { EmployeeContext } from '../contexts/EmployeeContext';
import { useContext, useState } from 'react';

const AddForm = () => {
    const { addEmployee } = useContext(EmployeeContext);

    const [newEmployee, setNewEmployee] = useState({
        name: "", age: "", address: "", gender: "", about: ""
    });
    const convertBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file)
            fileReader.onload = () => {
                resolve(fileReader.result);
            }
            fileReader.onerror = (error) => {
                reject(error);
            }
        })
    }
    const onInputChange = (e) => {
        setNewEmployee({ ...newEmployee, [e.target.name]: e.target.value })
    }
    const onImageChange = async e => {
        setNewEmployee({ ...newEmployee, image: await convertBase64(e.target.files[0]) })
    }
    const { name, age, address, gender, about } = newEmployee;

    const handleSubmit = (e) => {
        e.preventDefault();
        addEmployee(name, age, address, gender, about);
    }
    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Name *"
                    name="name"
                    value={name}
                    onChange={(e) => onInputChange(e)}
                    required
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Age *"
                    name="age"
                    value={age}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Address *"
                    name="address"
                    value={address}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Gender *"
                    name="gender"
                    value={gender}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="About *"
                    name="about"
                    value={about}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <div className="form-group">
                    <input type="file" multiple className="form-control form-control-md" name="image" id="file" onChange={e => onImageChange(e)} />
                </div>
            </div>
            <Button variant="success" type="submit">Add New Employee</Button>
        </form>
    )
}
export default AddForm;
