import { Button } from "react-bootstrap";
import { EmployeeContext } from '../contexts/EmployeeContext';
import { useContext, useState } from 'react';

const AddForm = () => {
    const { addEmployee } = useContext(EmployeeContext);

    const [newEmployee, setNewEmployee] = useState({
        name: "", age: "", address: "", gender: "", about: "", image: ""
    });
    const convertBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file)
            fileReader.onload = () => {
                resolve(fileReader.result);
            }
            fileReader.onerror = (error) => {
                reject(error);
            }
        })
    }
    const onInputChange = (e) => {
        setNewEmployee({ ...newEmployee, [e.target.name]: e.target.value })
    }
    const onImageChange = async e => {
        setNewEmployee({ ...newEmployee, image: await convertBase64(e.target.files[0]) })
    }
    const { name, age, address, gender, about, image } = newEmployee;

    const handleSubmit = (e) => {
        e.preventDefault();
        addEmployee(name, age, address, gender, about, image);
    }
    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="name">Name</label>
                <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={name}
                    onChange={(e) => onInputChange(e)}
                    required
                />
                <label htmlFor="age">Age</label>
                <input
                    type="text"
                    className="form-control"
                    name="age"
                    value={age}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <select className="form-group" name="address" value={address} onChange={e => onInputChange(e)}>
                    <option className="form-control">Select your location</option>
                    <option className="form-control">Chennai</option>
                    <option className="form-control">Kolkata</option>
                    <option className="form-control">Delhi</option>
                </select>
                <div className="form-group" name="gender" value={gender} onChange={e => onInputChange(e)}>
                    <label htmlFor="gender">Gender</label>
                    <input type="radio" id="male" name="gender" value="Male" />
                    <label htmlFor="Male">Male</label>
                    <input type="radio" id="female" name="gender" value="Female" />
                    <label htmlFor="Female">Female</label>
                </div>
                <input
                    type="text"
                    className="form-control"
                    placeholder="About *"
                    name="about"
                    value={about}
                    required
                    onChange={(e) => onInputChange(e)}
                />
                <div className="form-group">
                    <input type="file" multiple className="form-control" name="image" id="file" onChange={e => onImageChange(e)} />
                </div>
            </div>
            <Button variant="success" type="submit">Add New Employee</Button>
        </form>
    )
}
export default AddForm;
