import React, { useState } from 'react'
function Login() {
    const [user, setUser] = useState("");
    const [age, setAge] = useState("");

    const [userErr, setUserErr] = useState(false);
    const [ageErr, setAgeErr] = useState(false);

    function loginHandle(e) {
        e.preventDefault()
    }
    function userHandler(e) {
        let item = e.target.value;
        if (item.length < 3) {
            setUserErr(true)
        }
        else {
            setUserErr(false)
        }
        console.warn(e.target.value.length)
    }
    function ageHandler(e) {
        let item = e.target.value;
        if (item.length < 2) {
            setAgeErr(true)
        }
        else {
            setAgeErr(false)
        }
        console.warn(e.target.value.length)
    }
    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={loginHandle}>
                <label for="name">Enter your name</label>
                <input type="text" onChange={userHandler} />{userErr ? <span>User not valid</span> : ""}
                <br />
                <label for="age">Age</label>
                <input type="number" onChange={ageHandler} />{ageErr ? <span>Age not valid</span> : ""}
                <br />
                <label for="name">Address</label>
                <input type="text" onChange={userHandler} />{userErr ? <span>User not valid</span> : ""}

                <select class="select-menu">
                    <option>Location</option>
                    <option>2nd option</option>
                    <option>3rd option</option>
                    <option>4th option</option>
                    <option>5th option</option>
                </select>
                <br />
                <label for="name">About</label>
                <input type="text" onChange={userHandler} />{userErr ? <span>User not valid</span> : ""}
                <br />
                <label for="Gender">Gender: </label>
                <input type="radio" id="Male" name="fav_language" value="Male" />
                <label for="Male">Male</label>
                <input type="radio" id="Female" name="fav_language" value="Female" />
                <label for="Female">Female</label>
<br />

            </form>
        </div>
    )
}
export default Login;

