import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

const AddUser = () => {
    let history = useHistory();
    const [user, setUser] = useState({
        name: "",
        age: "",
        address: "",
        gender: "",
        about: "",
        image: ""
    });
    const [selectedImages, setSelectedImage] = useState([])
    const { name, age, address, gender, about, image } = user;
    const onInputChange = e => {
        setUser({ ...user, [e.target.name]: e.target.value })
    }

    const imageHandleChange = e => {
        if (e.target.files) {
            const fileArray = Array.from(e.target.files).map((file) => URL.createObjectURL(file))
            console.log(fileArray)

            setSelectedImage((prevImages) => prevImages.concat(fileArray))
        }

    }
    const onSubmit = async e => {
        e.preventDefault();
        await axios.post("http://localhost:3002/users", user);
        history.push("/");
    };

    return (
        <div className="container">
            <div className="w-75 mx-auto shadow p-5">
                <h2 className="text-center mb-4">Add A User</h2>
                <form onSubmit={e => onSubmit(e)}>
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your Name" name="name" value={name} onChange={e => onInputChange(e)} />
                    </div>
                    <br />
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your age" name="age" value={age} onChange={e => onInputChange(e)} />
                    </div>
                    <br />
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your address" name="address" value={address} onChange={e => onInputChange(e)} />
                    </div>
                    <br />
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your gender" name="gender" value={gender} onChange={e => onInputChange(e)} />
                    </div>
                    <br />
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="about" name="about" value={about} onChange={e => onInputChange(e)} />
                    </div>
                    <br />
                    <div className="form-group">
                        <input type="file" multiple className="form-control form-control-lg" name="image" id="file" onChange={imageHandleChange} />
                    </div>
                    <br />
                    <button className="btn btn-primary btn-block">Add User</button>
                </form>
            </div>
        </div>
    )
}
export default AddUser;
