import React, { useState } from 'react'

const MultipleInputs = () => {
    const [userRegistration, setUserRegistration] = useState({
        username: "",
        age: "",
        address: "",
        about: ""
    });

    const [records, setRecords] = useState([]);

    const handleInput = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        console.log(name, value);

        setUserRegistration({ ...userRegistration, [name]: value });
    }
    const handleSubmit = (e) => {
        e.preventDefault();

        const newRecord = { ...userRegistration, id: new Date().getTime().toString() }
        console.log(records);
        setRecords([...records, newRecord]);
        console.log(records);

        setUserRegistration({ username: "", age: "", address: "", about: "" })
    }

    return (
        <>
            <form action="" onSubmit={handleSubmit}>
                <h1>Login</h1>
                <div>
                    <label htmlFor="username">Enter your name</label>
                    <input type="text" autoComplete="off" value={userRegistration.username} onChange={handleInput} name="username" id="username" />
                </div>
                <br />
                <div>
                    <label htmlFor="age">Age</label>
                    <input type="number" autoComplete="off" value={userRegistration.age} onChange={handleInput} name="age" id="age" />
                </div>
                <div>
                    <label htmlFor="address">Address</label>
                    <input type="text" autoComplete="off" value={userRegistration.address} onChange={handleInput} name="address" id="address" />
                </div>
                <div>
                    <select className="select-menu">
                        <option>Location</option>
                        <option>Kolkata</option>
                        <option>Delhi</option>
                        <option>Mumbai</option>
                        <option>Bangalore</option>
                    </select>
                </div>
                <div>
                    <label htmlFor="about">About</label>
                    <input type="text" autoComplete="off" value={userRegistration.about} onChange={handleInput} name="about" id="about" />
                </div>
                <div>
                    <label htmlFor="Gender">Gender: </label>
                    <input type="radio" id="Male" name="fav_language" value="Male" />
                    <label htmlFor="Male">Male</label>
                    <input type="radio" id="Female" name="fav_language" value="Female" />
                    <label htmlFor="Female">Female</label>
                </div>
                <button type="submit">Registration</button>
            </form>
            <div>
                {
                    records.map((curElem) => {
                        const { id, username, age, address, about } = curElem;
                        return (
                            <div className="showDataStyle" key={id}>
                                <p>{username}</p>
                                <p>{age}</p>
                                <p>{address}</p>
                                <p>{about}</p>
                            </div>
                        )
                    })
                }
            </div>
        </>
    )
}
export default MultipleInputs;
