import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useHistory, useParams } from "react-router-dom";

const EditUser = () => {
    let history = useHistory();
    const { id } = useParams();
    const [user, setUser] = useState({
        name: "",
        age: "",
        address: "",
        gender: "",
        about: "",
        image: ""
    });

    const { name, age, address, gender, about, image } = user;
    const onInputChange = e => {
        setUser({ ...user, [e.target.name]: e.target.value })
    }
    useEffect(() => {
        loadUser();
    }, []);

    const onSubmit = async e => {
        e.preventDefault();
        await axios.put(`http://localhost:3002/users/${id}`, user);
        history.push("/");
    };

    const loadUser = async () => {
        const result = await axios.get(`http://localhost:3002/users/${id}`);
        setUser(result.data);
    }
    return (
        <div className="container">
            <div className="w-75 mx-auto shadow p-5">
                <h2 className="text-center mb-4">Edit A User</h2>
                <form onSubmit={e => onSubmit(e)}>
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your Name" name="name" value={name} onChange={e => onInputChange(e)} />
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your age" name="age" value={age} onChange={e => onInputChange(e)} />
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your address" name="address" value={address} onChange={e => onInputChange(e)} />
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="Enter Your gender" name="phone" value={gender} onChange={e => onInputChange(e)} />
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-control form-control-lg" placeholder="About" name="about" value={about} onChange={e => onInputChange(e)} />
                    </div>
                    <div className="form-group">
                        <input type="file" multiple className="form-control form-control-md" name="image" value- id="file" onChange={e => onInputChange(e)} />
                        {/* {renderPhotos(selectedImages)} */}
                    </div>
                    <button className="btn btn-primary btn-block">Update User</button>
                </form>
            </div>
        </div>
    )
}
export default EditUser;
